contracts/Airdrop.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./lib/AccessControl.sol";

contract Airdrop is AccessControl, ReentrancyGuard {
    struct AirdropData {
        address account;
        uint256 amount;
        uint256 claimedAmount;
        uint64 claimIndex;
    }

    uint256 public constant PRECISION = 1e12;

    address public tokenAddress;
    uint256 public startTimestamp;
    uint256 public endTimestamp;
    uint256 public tgePercent;
    uint64 public vestingCount;
    uint256 public vestingTerm;

    uint256 public totalAirdropAmount;

    uint256 public dataLength;
    mapping(uint256 => AirdropData) public airdropData;
    mapping(address => uint256) public airdropIndex;

    event Claimed(address indexed receiver, uint256 amount, uint256 timestamp);
    event AirdropStartTimeUpdated(uint256 newStartTime);
    event AirdropEndTimeUpdated(uint256 newEndTime);

    /**
     * @notice 생성자
     * @param _tokenAddress 에어드랍 토큰 주소
     * @param _startTimestamp 에어드랍 시작 시간
     * @param _endTimestamp 에어드랍 종료 시간
     * @param _tgePercent TGE 비율 (0 ~ 100)
     * @param _vestingCount tge 이후 클레임 횟수
     */
    constructor(
        address _tokenAddress,
        uint256 _startTimestamp,
        uint256 _endTimestamp,
        uint256 _tgePercent,
        uint64 _vestingCount
    ) {
        require(_startTimestamp < _endTimestamp, "INVALID_TIMESTAMP");
        tokenAddress = _tokenAddress;
        startTimestamp = _startTimestamp;
        endTimestamp = _endTimestamp;
        tgePercent = _tgePercent;
        vestingCount = _vestingCount;
        vestingTerm = (_endTimestamp - _startTimestamp) / (_vestingCount + 1);

        _grantRole(ADMIN_ROLE, msg.sender);
    }

    /**
     * @notice 에어드랍이 시작되었는지 확인합니다.
     */
    function isStarted() public view returns (bool) {
        return block.timestamp >= startTimestamp;
    }

    /**
     * @notice 에어드랍이 종료되었는지 확인합니다.
     */
    function isEnded() public view returns (bool) {
        return block.timestamp >= endTimestamp;
    }

    /**
     * @notice 에어드랍 데이터가 유효한지 확인합니다.
     * @param index 에어드랍 데이터 인덱스
     */
    function isValidAirdropId(uint256 index) public view returns (bool) {
        return index > 0 && index <= dataLength;
    }

    /**
     * @notice 전체 에어드랍 데이터를 조회합니다.
     * @return data 전체 에어드랍 데이터
     */
    function getAllAirdropData() public view returns (AirdropData[] memory data) {
        data = new AirdropData[](dataLength);
        for (uint256 i = 1; i <= dataLength; i++) {
            data[i - 1] = airdropData[i];
        }
    }

    /**
     * @notice 클레임 완료된 계정을 조회합니다.
     * @return data 클레임 완료된 계정 데이터
     */
    function getFullyClaimedAccounts() public view returns (AirdropData[] memory data) {
        uint256 count = 0;
        for (uint256 i = 1; i <= dataLength; i++) {
            if (airdropData[i].claimedAmount == airdropData[i].amount) {
                count++;
            }
        }

        data = new AirdropData[](count);

        uint256 index = 0;
        for (uint256 i = 1; i <= dataLength; i++) {
            if (airdropData[i].claimedAmount == airdropData[i].amount) {
                data[index] = airdropData[i];
                index++;
            }
        }
    }

    /**
     * @notice 호출한 사람의 에어드랍 클레임 가능한 금액을 계산합니다.
     * @param account 클레임 가능한 금액을 계산할 계정
     * @return claimAmount 클레임 가능한 금액
     */
    function getClaimableAmount(address account) public view returns (uint256 claimAmount) {
        uint256 index = airdropIndex[account];
        require(isValidAirdropId(index), "NO_AIRDROP_DATA");

        (, claimAmount) = getClaimAmount(index);
    }

    /**
     * @notice 에어드랍 클레임 가능한 금액을 계산합니다.
     * @param index 에어드랍 데이터 인덱스
     * @return claimIndex 클레임 인덱스
     * @return claimAmount 클레임 가능한 금액
     */
    function getClaimAmount(uint256 index) internal view returns (uint64 claimIndex, uint256 claimAmount) {
        AirdropData memory airdrop = airdropData[index];

        /*
        vestingCount는 tge 이후 보상 횟수이므로, 에어드랍 전체 기간을 vestingCount + 1로 나누어야 합니다.

        예를 들어,
        startTimestamp = 0, endTimestamp = 5, vestingCount = 4 라면 vestingTerm = (5 - 0) / (4 + 1) = 1

        |-----------------|-----------------|-----------------|-----------------|-----------------|
        0                 1                 2                 3                 4                 x
        tge               vesting1          vesting2          vesting3          vesting4          end

        위와 같이 5개의 구간으로 나누어져야 하며, 각 구간의 금액은 totalClaimAmount * (100 - tgePercent) / 100 / vestingCount 입니다.
        */

        uint256 term = block.timestamp - startTimestamp;
        claimIndex = uint64(term / vestingTerm);

        uint256 claimableIndex = claimIndex - airdrop.claimIndex;
        uint256 vestingAmountPerTerm = (airdrop.amount * PRECISION * (100 - tgePercent)) / 100 / vestingCount;
        claimAmount = (vestingAmountPerTerm * claimableIndex) / PRECISION;

        // TGE를 클레임하지 않은 경우, TGE 금액 추가
        if (airdrop.claimIndex == 0 && airdrop.claimedAmount == 0) {
            uint256 tgeAmountX12 = (airdrop.amount * PRECISION * tgePercent) / 100;
            claimAmount += tgeAmountX12 / PRECISION;
        }

        // 마지막 클레임 구간인 경우, 남은 금액 전부를 전송
        if (claimIndex == vestingCount) {
            uint256 remain = airdrop.amount - airdrop.claimedAmount;
            claimAmount = remain;
        }
    }

    /**
     * @notice 에어드랍을 클레임합니다.
     */
    function claim() external nonReentrant {
        require(block.timestamp >= startTimestamp, "AIRDOP_NOT_STARTED");
        require(block.timestamp < endTimestamp, "AIRDROP_ENDED");

        uint256 index = airdropIndex[msg.sender];
        require(isValidAirdropId(index), "NO_AIRDROP_DATA");

        AirdropData storage airdrop = airdropData[index];

        (uint64 claimIndex, uint256 claimAmount) = getClaimAmount(index);
        require(claimAmount > 0, "NO_CLAIMABLE_AMOUNT");

        uint256 balance = IERC20(tokenAddress).balanceOf(address(this));
        require(balance >= claimAmount, "INSUFFICIENT_BALANCE");

        IERC20(tokenAddress).transfer(msg.sender, claimAmount);
        airdrop.claimedAmount += claimAmount;
        airdrop.claimIndex = claimIndex;

        emit Claimed(msg.sender, claimAmount, block.timestamp);
    }

    /**
     * @notice 에어드랍 데이터를 조회합니다.
     * @return totalAmount 총 에어드랍 금액
     * @return claimedAmount 클레임된 금액
     * @return claimableAmount 클레임 가능한 금액
     */
    function getAirdropData()
        public
        view
        returns (uint256 totalAmount, uint256 claimedAmount, uint256 claimableAmount)
    {
        return getAirdropDataByAddress(msg.sender);
    }

    /**
     * @notice 에어드랍 데이터를 조회합니다.
     * @param _address 조회할 계정 주소
     * @return totalAmount 총 에어드랍 금액
     * @return claimedAmount 클레임된 금액
     * @return claimableAmount 클레임 가능한 금액
     */
    function getAirdropDataByAddress(
        address _address
    ) public view returns (uint256 totalAmount, uint256 claimedAmount, uint256 claimableAmount) {
        uint256 index = airdropIndex[_address];
        require(isValidAirdropId(index), "NO_AIRDROP_DATA");

        AirdropData memory airdrop = airdropData[index];
        totalAmount = airdrop.amount;
        claimedAmount = airdrop.claimedAmount;

        (, claimableAmount) = getClaimAmount(index);
    }

    /**
     * @notice 에어드랍 데이터를 추가합니다.
     * @param _account 조회할 계정 주소
     * @param _amount 에어드랍 개수
     */
    function insertAirdropData(address _account, uint256 _amount) public onlyRole(ADMIN_ROLE) {
        uint256 index = airdropIndex[_account];
        require(index == 0, "DUPLICATED_DATA");

        dataLength++;
        airdropIndex[_account] = dataLength;

        airdropData[dataLength] = AirdropData({account: _account, amount: _amount, claimedAmount: 0, claimIndex: 0});

        totalAirdropAmount += _amount;
    }

    /**
     * @notice 에어드랍 데이터를 추가합니다.
     * @param _accounts[] 조회할 계정 주소
     * @param _amounts[] 에어드랍 개수
     */
    function batchInsertAirdropData(address[] memory _accounts, uint256[] memory _amounts) public onlyRole(ADMIN_ROLE) {
        require(_accounts.length == _amounts.length, "INVALID_INPUT_DATA");

        for (uint256 i = 0; i < _accounts.length; i++) {
            // insertAirdropData(_accounts[i], _amounts[i]);
            uint256 index = airdropIndex[_accounts[i]];
            require(index == 0, "DUPLICATED_DATA");

            dataLength++;
            airdropIndex[_accounts[i]] = dataLength;

            airdropData[dataLength] = AirdropData({
                account: _accounts[i],
                amount: _amounts[i],
                claimedAmount: 0,
                claimIndex: 0
            });

            totalAirdropAmount += _amounts[i];
        }
    }

    /**
     * @notice 에어드랍 데이터를 업데이트합니다.
     * @param _address 계정 주소
     * @param _airdropData 에어드랍 데이터
     */
    function updateAirdropData(address _address, AirdropData memory _airdropData) public onlyRole(ADMIN_ROLE) {
        uint256 index = airdropIndex[_address];
        require(index > 0, "NO_AIRDROP_DATA");

        airdropData[index] = _airdropData;
    }

    /**
     * @notice 에어드랍 데이터를 삭제합니다.
     * @param _address 계정 주소
     */
    function deleteAirdropData(address _address) public onlyRole(ADMIN_ROLE) {
        uint256 index = airdropIndex[_address];
        require(index > 0, "NO_AIRDROP_DATA");

        delete airdropData[index];
        delete airdropIndex[_address];
    }

    /**
     * @notice 에어드랍 토큰 잔액 전부를 owner에게 전송합니다. 컨트랙트 owner만 호출 가능.
     */
    function collect() public onlyRole(ADMIN_ROLE) {
        uint256 balance = IERC20(tokenAddress).balanceOf(address(this));
        IERC20(tokenAddress).transfer(msg.sender, balance);
    }

    /**
     * @notice 에어드랍 시작 시간을 업데이트합니다. 컨트랙트 owner만 호출 가능.
     * @param _startTimestamp 새로운 에어드랍 시작 시간
     */
    function updateStartTimestamp(uint256 _startTimestamp) public onlyRole(ADMIN_ROLE) {
        startTimestamp = _startTimestamp;
        vestingTerm = (endTimestamp - startTimestamp) / (vestingCount + 1);
        emit AirdropStartTimeUpdated(_startTimestamp);
    }

    /**
     * @notice 에어드랍 종료 시간을 업데이트합니다. 컨트랙트 owner만 호출 가능.
     * @param _endTimestamp 새로운 에어드랍 종료 시간
     */
    function updateEndTimestamp(uint256 _endTimestamp) public onlyRole(ADMIN_ROLE) {
        endTimestamp = _endTimestamp;
        vestingTerm = (endTimestamp - startTimestamp) / (vestingCount + 1);
        emit AirdropEndTimeUpdated(_endTimestamp);
    }
}

contracts/AirdropFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "./lib/AccessControl.sol";
import "./Airdrop.sol";

contract AirdropFactory is AccessControl {

    struct AirdropInfo {
        address airdrop;            // 에어드랍 주소
        address token;              // 에어드랍 토큰 주소
        uint256 startTimestamp;     // 에어드랍 시작 시간
        uint256 endTimestamp;       // 에어드랍 종료 시간
        uint256 tgePercent;         // TGE 토큰 비율
        uint256 vestingCount;       // TGE 이후 클레임 횟수
    }

    uint256 public airdropLength;
    mapping(address => uint256) public airdrops;
    mapping(uint256 => AirdropInfo) public airdropInfo;

    event AirdropCreated(address indexed airdrop, address indexed token, uint256 startTimestamp, uint256 endTimestamp);

    constructor() {
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    /**
     * @notice 에어드랍 컨트랙트를 생성합니다.
     * @param token 에어드랍 토큰 주소
     * @param startTimestamp 에어드랍 시작 시간
     * @param endTimestamp 에어드랍 종료 시간
     * @param tgePercent TGE 토큰 비율
     * @param vestingCount TGE 이후 클레임 횟수
     * @return 생성된 에어드랍 컨트랙트의 id
     */
    function createAirdrop(
        address token,
        uint256 startTimestamp, 
        uint256 endTimestamp,
        uint256 tgePercent,
        uint64 vestingCount
    ) public onlyRole(ADMIN_ROLE) returns (uint256) {
        airdropLength++;

        Airdrop newAirDrop = new Airdrop(token, startTimestamp, endTimestamp, tgePercent, vestingCount);
        newAirDrop.grantRole(ADMIN_ROLE, msg.sender);

        address airdropAddress = address(newAirDrop);
        airdrops[airdropAddress] = airdropLength;
        airdropInfo[airdropLength] = AirdropInfo({
            airdrop: airdropAddress,
            token: token,
            startTimestamp: startTimestamp,
            endTimestamp: endTimestamp,
            tgePercent: tgePercent,
            vestingCount: vestingCount
        });

        emit AirdropCreated(airdropAddress, token, startTimestamp, endTimestamp);

        return airdropLength;
    }

    /**
     * @notice 모든 에어드랍 정보를 조회합니다.
     * @return 모든 에어드랍 정보
     */
    function getAllAirdrops() external view returns (AirdropInfo[] memory) {
        AirdropInfo[] memory result = new AirdropInfo[](airdropLength);
        for (uint256 i = 1; i <= airdropLength; i++) {
            result[i - 1] = airdropInfo[i];
        }
        return result;
    }

    /**
     * @notice 특정 에어드랍 정보를 조회합니다.
     * @param airdrop 에어드랍 주소
     */
    function getAirdropByAddress(address airdrop) external view returns (AirdropInfo memory) {
        return airdropInfo[airdrops[airdrop]];
    }
}

contracts/AirdropLock.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./lib/AccessControl.sol";

contract AirdropLock is AccessControl, ReentrancyGuard {
    struct AirdropInfo {
        uint256 amount;
        uint256 claimedAmount;
        uint64 lockupEndTimestamp;
        bool claimed;
    }

    uint16 public constant PERCENT_PRECISION = 1e4;

    IERC20 public token;

    uint64 public startTimestamp;
    uint64 public endTimestamp;
    uint64 public lockupPeriod;
    uint16 public immediateClaimPercentage;

    uint256 public dataLength;
    mapping(uint256 => AirdropInfo) public airdropInfo;
    mapping(address => uint256) public airdropIndex;

    uint256 public totalAirdropAmount;

    event AirdropClaimed(address indexed user, uint256 amount);
    event Lockup(address indexed user, uint256 lockupEndTimestamp);
    event AirdropClosed();

    constructor(
        address _token,
        uint64 _startTimestamp,
        uint64 _endTimestamp,
        uint64 _lockupPeriod,
        uint16 _immediateClaimPercentage
    ) {
        require(_token != address(0), "Airdrop: Invalid token address");
        require(_startTimestamp < _endTimestamp, "Airdrop: Invalid start and end timestamp");
        require(_endTimestamp < _startTimestamp + _lockupPeriod, "Airdrop: Invalid lockup period");
        require(_immediateClaimPercentage <= PERCENT_PRECISION, "Airdrop: Invalid immediate claim percentage");

        token = IERC20(_token);
        startTimestamp = _startTimestamp;
        endTimestamp = _endTimestamp;
        immediateClaimPercentage = _immediateClaimPercentage;
        lockupPeriod = _lockupPeriod;

        _grantRole(ADMIN_ROLE, msg.sender);
    }

    modifier onlyAirdropOpen() {
        require(block.timestamp >= startTimestamp, "Airdrop: Airdrop not started");
        require(block.timestamp < endTimestamp, "Airdrop: Airdrop ended");
        _;
    }

    function setImmediateClaimPercentage(uint16 _immediateClaimPercentage) external onlyRole(ADMIN_ROLE) {
        immediateClaimPercentage = _immediateClaimPercentage;
    }

    function setLockupPeriod(uint64 _lockupPeriod) external onlyRole(ADMIN_ROLE) {
        lockupPeriod = _lockupPeriod;
    }

    function setStartTimestamp(uint64 _startTimestamp) external onlyRole(ADMIN_ROLE) {
        startTimestamp = _startTimestamp;
    }

    function setEndTimestamp(uint64 _endTimestamp) external onlyRole(ADMIN_ROLE) {
        endTimestamp = _endTimestamp;
    }

    function getAirdropInfo() external view returns (AirdropInfo memory) {
        return airdropInfo[airdropIndex[msg.sender]];
    }

    function insertAirdropData(address receiver, uint256 amount) external onlyRole(ADMIN_ROLE) {
        require(airdropIndex[receiver] == 0, "Airdrop: Airdrop data already exists");

        dataLength++;
        airdropIndex[receiver] = dataLength;
        airdropInfo[dataLength] = AirdropInfo(amount, 0, 0, false);

        totalAirdropAmount += amount;
    }

    function batchInsertAirdropData(
        address[] calldata receivers,
        uint256[] calldata amounts
    ) external onlyRole(ADMIN_ROLE) {
        require(receivers.length == amounts.length, "Airdrop: Receivers and amounts length mismatch");

        for (uint256 i = 0; i < receivers.length; i++) {
            address receiver = receivers[i];
            uint256 amount = amounts[i];

            require(airdropIndex[receiver] == 0, "Airdrop: Airdrop data already exists");

            dataLength++;
            airdropIndex[receiver] = dataLength;
            airdropInfo[dataLength] = AirdropInfo(amount, 0, 0, false);

            totalAirdropAmount += amount;
        }
    }

    function updateAirdropData(address receiver, uint256 amount) external onlyRole(ADMIN_ROLE) {
        uint256 index = airdropIndex[receiver];
        require(index > 0, "Airdrop: Airdrop data not found");

        AirdropInfo storage info = airdropInfo[index];
        totalAirdropAmount -= info.amount;
        totalAirdropAmount += amount;

        info.amount = amount;
    }

    function deleteAirdropData(address receiver) external onlyRole(ADMIN_ROLE) {
        uint256 index = airdropIndex[receiver];
        require(index > 0, "Airdrop: Airdrop data not found");

        totalAirdropAmount -= airdropInfo[index].amount;

        delete airdropIndex[receiver];
        delete airdropInfo[index];
    }

    function claimAirdrop() external onlyAirdropOpen nonReentrant {
        address receiver = msg.sender;
        uint256 index = airdropIndex[receiver];
        require(index > 0, "Airdrop: Airdrop data not found");

        AirdropInfo storage info = airdropInfo[index];
        require(!info.claimed, "Airdrop: Airdrop already claimed");
        require(info.amount > 0, "Airdrop: No airdrop available");

        uint256 claimAmount = (info.amount * immediateClaimPercentage) / PERCENT_PRECISION;
        uint256 burnAmount = info.amount - claimAmount;
        token.transfer(receiver, claimAmount);
        token.transfer(address(0), burnAmount);

        info.claimed = true;
        info.claimedAmount = claimAmount;

        emit AirdropClaimed(receiver, claimAmount);
    }

    function lockup() external onlyAirdropOpen nonReentrant {
        address receiver = msg.sender;
        uint256 index = airdropIndex[receiver];
        require(index > 0, "Airdrop: Airdrop data not found");

        AirdropInfo storage info = airdropInfo[index];
        require(!info.claimed, "Airdrop: Airdrop already claimed");
        require(info.amount > 0, "Airdrop: No airdrop available");

        info.lockupEndTimestamp = uint64(block.timestamp + lockupPeriod);

        emit Lockup(receiver, info.lockupEndTimestamp);
    }

    function claimLockup() external nonReentrant {
        address receiver = msg.sender;
        uint256 index = airdropIndex[receiver];
        require(index > 0, "Airdrop: Airdrop data not found");

        AirdropInfo storage info = airdropInfo[index];
        require(!info.claimed, "Airdrop: Airdrop already claimed");
        require(info.amount > 0, "Airdrop: No airdrop available");
        require(
            info.lockupEndTimestamp > 0 && block.timestamp >= info.lockupEndTimestamp,
            "Airdrop: Lockup period not over"
        );

        token.transfer(receiver, info.amount);
        info.claimed = true;
        info.claimedAmount = info.amount;

        emit AirdropClaimed(receiver, info.amount);
    }

    function closeAirdrop() external onlyRole(ADMIN_ROLE) {
        token.transfer(msg.sender, token.balanceOf(address(this)));
        emit AirdropClosed();
    }
}

contracts/ChuruLaunchpad.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./lib/AccessControl.sol";

contract ChuruLaunchpad is AccessControl, ReentrancyGuard {
    uint32 public constant PERCENT_PRECISION = 1e8;
    address public churu;
    uint256 public amount;
    uint256 public claimRatio;

    uint64 public startBlock;
    uint64 public endBlock;

    event Enrolled(uint256 amount, uint256 claimRatio);
    event PeriodUpdated(uint64 startBlock, uint64 endBlock);
    event ClaimRatioUpdated(uint256 claimRatio);
    event LaunchpadAmountUpdated(uint256 amount);
    event Claimed(address indexed user, uint256 amount);
    event Closed(uint256 amount);

    constructor(address _churu) {
        churu = _churu;
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    function enroll(
        uint256 _amount,
        uint256 _claimRatio,
        uint64 _startBlock,
        uint64 _endBlock
    ) external onlyRole(ADMIN_ROLE) {
        require(_startBlock > block.number, "Launchpad: invalid start block");
        require(_endBlock > _startBlock, "Launchpad: invalid end block");

        amount = _amount;
        claimRatio = _claimRatio;
        startBlock = _startBlock;
        endBlock = _endBlock;

        IERC20(churu).transferFrom(msg.sender, address(this), _amount);

        emit Enrolled(_amount, _claimRatio);
    }

    function updatePeriod(uint64 _startBlock, uint64 _endBlock) external onlyRole(ADMIN_ROLE) {
        require(_endBlock > _startBlock, "Launchpad: invalid period");

        startBlock = _startBlock;
        endBlock = _endBlock;

        emit PeriodUpdated(_startBlock, _endBlock);
    }

    function updateChuruPerAce(uint256 _claimRatio) external onlyRole(ADMIN_ROLE) {
        claimRatio = _claimRatio;

        emit ClaimRatioUpdated(_claimRatio);
    }

    function updateLaunchpadAmount(uint256 _amount) external onlyRole(ADMIN_ROLE) {
        if (amount > _amount) {
            uint256 diff = amount - _amount;
            IERC20(churu).transfer(msg.sender, diff);
        } else if (amount < _amount) {
            uint256 diff = _amount - amount;
            IERC20(churu).transferFrom(msg.sender, address(this), diff);
        }

        amount = _amount;

        emit LaunchpadAmountUpdated(_amount);
    }

    function claim() external payable {
        require(block.number >= startBlock, "Launchpad: not started");
        require(block.number <= endBlock, "Launchpad: ended");

        uint256 value = msg.value;
        require(value > 0, "Launchpad: invalid value");

        uint256 churuAmount = (value * claimRatio) / 1e18;
        IERC20(churu).transfer(msg.sender, churuAmount);

        emit Claimed(msg.sender, churuAmount);
    }

    function getProgress() external view returns (uint256) {
        uint256 remain = IERC20(churu).balanceOf(address(this));
        return (remain * PERCENT_PRECISION) / amount;
    }

    function close() external onlyRole(ADMIN_ROLE) {
        uint256 balance = IERC20(churu).balanceOf(address(this));
        IERC20(churu).transfer(msg.sender, balance);

        endBlock = uint64(block.number);

        emit Closed(balance);
    }
}

contracts/ERC20Token.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract ERC20Token is ERC20 {
    constructor(string memory name_, string memory symbol_) ERC20(name_, symbol_) {
        _mint(msg.sender, 1000000 * 10 ** decimals());
    }

    function mint(address to, uint256 amount) public {
        _mint(to, amount);
    }

    function burn(uint256 amount) public {
        _burn(msg.sender, amount);
    }

    function transfer(address to, uint256 value) public override returns (bool) {
        require(value > 0, "ERC20: transfer amount must be greater than zero");
        require(balanceOf(msg.sender) >= value, "ERC20: transfer amount exceeds balance");

        _update(msg.sender, to, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) public override returns (bool) {
        require(value > 0, "ERC20: transfer amount must be greater than zero");
        require(balanceOf(from) >= value, "ERC20: transfer amount exceeds balance");

        _spendAllowance(from, msg.sender, value);
        _update(from, to, value);
        return true;
    }
}

contracts/lib/AccessControl.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;


contract AccessControl {
    bytes32 public constant ADMIN_ROLE = 0x00;

    mapping(bytes32 => mapping(address => bool)) private roles;

    event RoleGranted(bytes32 indexed role, address indexed account, address indexed admin);
    event RoleRevoked(bytes32 indexed role, address indexed account, address indexed admin);

    modifier onlyRole(bytes32 role) {
        require(hasRole(role, msg.sender), "AccessControl: sender must have the admin role");
        _;
    }

    function grantRole(bytes32 role, address account) public onlyRole(ADMIN_ROLE) {
        _grantRole(role, account);
    }

    function _grantRole(bytes32 role, address account) internal {
        roles[role][account] = true;
        emit RoleGranted(role, account, msg.sender);
    }

    function revokeRole(bytes32 role, address account) public onlyRole(ADMIN_ROLE) {
        _revokeRole(role, account);   
    }

    function _revokeRole(bytes32 role, address account) internal {
        roles[role][account] = false;
        emit RoleRevoked(role, account, msg.sender);
    }

    function hasRole(bytes32 role, address account) public view returns (bool) {
        return roles[role][account];
    }
}

