// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./lib/AccessControl.sol";

contract Spinner is AccessControl, ReentrancyGuard {
    // Constants
    uint256 public constant PRECISION = 1e18;
    uint256 public totalSegments; // Total number of segments in wheel

    // State variables
    mapping(uint256 => uint256) public segmentAmounts; // Amount allocated to each segment
    mapping(uint256 => uint256) public segmentRemaining; // Remaining amount for each segment
    uint256 public totalSpins; // Total number of spins performed
    bool public isActive;

    // User spin tracking
    mapping(address => uint256) public userSpinCount; // Number of spins per user
    mapping(address => mapping(uint256 => uint256)) public userSegmentHits; // Number of hits per segment per user

    // Events
    event SpinResult(
        address indexed user,
        uint256 segment,
        uint256 amount,
        uint256 timestamp
    );
    event SegmentUpdated(uint256 segment, uint256 amount);
    event SpinnerStatusChanged(bool isActive);
    event TotalSegmentsUpdated(uint256 totalSegments);

    constructor(uint256 _totalSegments) {
        _grantRole(ADMIN_ROLE, msg.sender);
        isActive = false;
        totalSegments = _totalSegments;
    }

    /**
     * @notice Set amounts for wheel segments
     * @param segments Array of segment indices
     * @param amounts Array of amounts for each segment
     */
    function setSegmentAmounts(
        uint256[] calldata segments,
        uint256[] calldata amounts
    ) external onlyRole(ADMIN_ROLE) {
        require(segments.length == amounts.length, "Length mismatch");

        for (uint256 i = 0; i < segments.length; i++) {
            require(segments[i] < totalSegments, "Invalid segment");
            segmentAmounts[segments[i]] = amounts[i];
            segmentRemaining[segments[i]] = amounts[i];
            emit SegmentUpdated(segments[i], amounts[i]);
        }
    }

    /**
     * @notice Set total number of segments
     * @param _totalSegments New total segments value
     */
    function setTotalSegments(
        uint256 _totalSegments
    ) external onlyRole(ADMIN_ROLE) {
        require(_totalSegments > 0, "Total segments must be greater than 0");
        totalSegments = _totalSegments;
        emit TotalSegmentsUpdated(_totalSegments);
    }

    /**
     * @notice Spin the wheel
     * @return segment The landed segment number
     * @return amount The amount won
     */
    function spin()
        external
        nonReentrant
        returns (uint256 segment, uint256 amount)
    {
        require(isActive, "Spinner not active");

        // Generate random segment using block data
        uint256 attempts = 0;
        bool validSegmentFound = false;

        while (!validSegmentFound && attempts < totalSegments) {
            require(attempts <= 30, "No valid segments available");

            segment =
                uint256(
                    keccak256(
                        abi.encodePacked(
                            block.timestamp,
                            block.prevrandao,
                            msg.sender,
                            totalSpins,
                            attempts
                        )
                    )
                ) %
                totalSegments;

            // If segment has unlimited amount (0) or has remaining amount, use it
            if (segmentAmounts[segment] == 0 || segmentRemaining[segment] > 0) {
                validSegmentFound = true;
            }
            attempts++;
        }

        require(validSegmentFound, "No valid segments available");

        amount = segmentAmounts[segment];
        if (amount > 0) {
            segmentRemaining[segment] -= amount;
        }
        totalSpins++;

        // Update user spin tracking
        userSpinCount[msg.sender]++;
        userSegmentHits[msg.sender][segment]++;

        emit SpinResult(msg.sender, segment, amount, block.timestamp);
    }

    /**
     * @notice Set spinner active status
     * @param _isActive New active status
     */
    function setActive(bool _isActive) external onlyRole(ADMIN_ROLE) {
        isActive = _isActive;
        emit SpinnerStatusChanged(_isActive);
    }

    /**
     * @notice Get remaining amounts for all segments
     * @return amounts Array of remaining amounts
     */
    function getRemainingAmounts()
        external
        view
        returns (uint256[] memory amounts)
    {
        amounts = new uint256[](totalSegments);
        for (uint256 i = 0; i < totalSegments; i++) {
            amounts[i] = segmentRemaining[i];
        }
    }
}

